"use client"

import { useState } from "react"
import axios from "axios"
import { Eye, EyeOff } from "lucide-react" // ðŸ‘ˆ eye icons for toggle

function Login({ navigateToHome, navigateToSignup }) {
  const [formData, setFormData] = useState({
    email: "",
    password: "",
  })

  const [errors, setErrors] = useState({
    email: "",
    password: "",
  })

  const [showPassword, setShowPassword] = useState(false) // ðŸ‘ˆ toggle state
  const [successMessage, setSuccessMessage] = useState("")
  const [errorMessage, setErrorMessage] = useState("")
  // Validation for email
  const validateEmail = (email) => {
    if (!email) return "Email is required"
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/
    if (!emailRegex.test(email)) return "Enter a valid email address"
    return ""
  }

  // Validation for password
  const validatePassword = (password) => {
    if (!password) return "Password is required"
    if (password.length < 6) return "Password must be at least 6 characters"
    return ""
  }

  const handleChange = (e) => {
    const { name, value } = e.target

    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }))

    // Validate on change
    if (name === "email") {
      setErrors((prev) => ({ ...prev, email: validateEmail(value) }))
    }
    if (name === "password") {
      setErrors((prev) => ({ ...prev, password: validatePassword(value) }))
    }
  }

  const handleSubmit = async (e) => {
    e.preventDefault()

    // Final check before submitting
    const emailError = validateEmail(formData.email)
    const passwordError = validatePassword(formData.password)

    if (emailError || passwordError) {
      setErrors({ email: emailError, password: passwordError })
      return
    }

    try {
      const response = await axios.post("http://localhost:5000/login", formData)
      localStorage.setItem("token", response.data.token)
      setSuccessMessage("Login successful!")
      setErrorMessage("")
      navigateToHome()
    } catch (error) {
      setErrorMessage(error.response?.data?.message || "Login failed. Please try again.")
      setSuccessMessage("")
    }
  }

  return (
    <div className="min-h-screen w-full flex justify-center items-center font-sans relative overflow-hidden">
      <div
        className="absolute inset-0 bg-cover bg-center"
        style={{ backgroundImage: "url('/hacker.jpg')" }}
      ></div>

      <div className="absolute inset-0 bg-gradient-to-b from-black/60 via-black/30 to-black/60"></div>

      <div className="absolute inset-0 animated-bg">
        <div className="floating-particles">
          {[...Array(12)].map((_, i) => (
            <div key={i} className={`particle particle-${i + 1}`}></div>
          ))}
        </div>
        <div className="gradient-orbs">
          <div className="orb orb-1"></div>
          <div className="orb orb-2"></div>
        </div>
      </div>

      {/* Enhanced Neumorphic card with backdrop blur */}
      <div className="bg-[#e0e5ec]/90 backdrop-blur-sm rounded-2xl p-10 flex flex-col items-center shadow-[10px_10px_20px_#a3b1c6,-10px_-10px_20px_#ffffff] hover:shadow-[12px_12px_24px_#a3b1c6,-12px_-12px_24px_#ffffff] transition-all duration-300 w-[450px] relative z-10">
        <h1 className="text-[#6d7582] text-3xl font-semibold mb-8">Login</h1>

        {successMessage && <p className="text-green-600 font-medium mb-4">{successMessage}</p>}
        {errorMessage && <p className="text-red-500 font-medium mb-4">{errorMessage}</p>}

        {/* Email Input */}
        <div className="w-full mb-8">
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={formData.email}
            onChange={handleChange}
            className="w-full p-4 bg-[#e0e5ec] rounded-xl shadow-[inset_10px_10px_20px_#a3b1c6,inset_-10px_-10px_20px_#ffffff] text-[#6d7582] placeholder-[#a3b1c6] focus:outline-none transition-all duration-300 text-lg"
          />
          {errors.email && <p className="text-red-500 text-sm mt-2 ml-1">{errors.email}</p>}
        </div>

        {/* Password Input with Toggle */}
        <div className="w-full mb-10 relative">
          <input
            type={showPassword ? "text" : "password"} // ðŸ‘ˆ toggle visibility
            name="password"
            placeholder="Password"
            value={formData.password}
            onChange={handleChange}
            className="w-full p-4 pr-12 bg-[#e0e5ec] rounded-xl shadow-[inset_10px_10px_20px_#a3b1c6,inset_-10px_-10px_20px_#ffffff] text-[#6d7582] placeholder-[#a3b1c6] focus:outline-none transition-all duration-300 text-lg"
          />
          <button
            type="button"
            onClick={() => setShowPassword((prev) => !prev)} // ðŸ‘ˆ toggle state
            className="absolute right-4 top-1/2 -translate-y-1/2 text-[#6d7582] hover:text-[#4b5563] transition-colors"
          >
            {showPassword ? <EyeOff size={22} /> : <Eye size={22} />}
          </button>
          {errors.password && <p className="text-red-500 text-sm mt-2 ml-1">{errors.password}</p>}
        </div>

        {/* Submit Button - made slightly larger */}
        <button
          type="submit"
          onClick={handleSubmit}
          className="px-10 py-4 bg-[#e0e5ec] rounded-xl shadow-[10px_10px_20px_#a3b1c6,-10px_-10px_20px_#ffffff] text-[#6d7582] hover:shadow-[0_0_10px_#4b658b,0_0_20px_#ad2424] hover:-translate-y-0.5 transition-all duration-300 active:shadow-[inset_5px_5px_10px_#a3b1c6,inset_-5px_-5px_10px_#ffffff] text-lg font-medium"
        >
          Login
        </button>

        {/* Signup Link */}
        <p className="text-[#6d7582] text-base mt-8">
          Don't have an account?{" "}
          <button
            type="button"
            onClick={navigateToSignup}
            className="text-[#6d7582] font-medium hover:underline focus:outline-none hover:text-[#5a6473] transition-colors duration-300"
          >
            Sign up
          </button>
        </p>
      </div>

