import React, { useState } from "react";
import axios from "axios";
import { Eye, EyeOff } from "lucide-react"; // ðŸ‘ˆ eye icons for toggle


function Signup({ navigateToLogin }) {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    phoneNumber: ""
  });

  const [errors, setErrors] = useState({
    name: "",
    email: "",
    password: "",
    phoneNumber: ""
  });

  const [showPassword, setShowPassword] = useState(false); // ðŸ‘ˆ toggle state

  // Validation functions (unchanged)
  const validateName = (name) => {
    if (!name) return "Name is required";
    if (name.length < 3) return "Name must be at least 3 characters";
    return "";
  };

  const validateEmail = (email) => {
    if (!email) return "Email is required";
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(email)) return "Enter a valid email address";
    return "";
  };

  const validatePassword = (password) => {
    if (!password) return "Password is required";
    if (password.length < 6) return "Password must be at least 6 characters";
    return "";
  };

  const validatePhoneNumber = (phone) => {
    if (!phone) return "Phone number is required";
    const phoneRegex = /^[0-9]{10}$/;
    if (!phoneRegex.test(phone)) return "Enter a valid 10-digit phone number";
    return "";
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });

    // Real-time validation
    if (name === "name") setErrors((prev) => ({ ...prev, name: validateName(value) }));
    if (name === "email") setErrors((prev) => ({ ...prev, email: validateEmail(value) }));
    if (name === "password") setErrors((prev) => ({ ...prev, password: validatePassword(value) }));
    if (name === "phoneNumber") setErrors((prev) => ({ ...prev, phoneNumber: validatePhoneNumber(value) }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Final validation before submission
    const nameError = validateName(formData.name);
    const emailError = validateEmail(formData.email);
    const passwordError = validatePassword(formData.password);
    const phoneError = validatePhoneNumber(formData.phoneNumber);

    if (nameError || emailError || passwordError || phoneError) {
      setErrors({
        name: nameError,
        email: emailError,
        password: passwordError,
        phoneNumber: phoneError
      });
      return;
    }

    try {
      await axios.post("http://localhost:5000/signup", formData);
      alert("Signup successful");
      navigateToLogin();
    } catch (error) {
      if (error.response?.status === 400) {
        if (window.confirm("User already exists. Do you want to login instead?")) {
          navigateToLogin();
        }
      }
    }
  };

  return (
    <div className="min-h-screen w-full flex justify-center items-center font-sans">
      {/* Neumorphic card */}
      <div className="bg-[#e0e5ec] rounded-2xl p-8 flex flex-col items-center shadow-[10px_10px_20px_#a3b1c6,-10px_-10px_20px_#ffffff] hover:shadow-[12px_12px_24px_#a3b1c6,-12px_-12px_24px_#ffffff] transition-all duration-300 w-[400px]">
        <h1 className="text-[#6d7582] text-2xl font-semibold mb-6">Sign Up</h1>
        
        {/* Name Input */}
        <div className="w-full mb-6">
          <input
            type="text"
            name="name"
            placeholder="Name"
            value={formData.name}
            onChange={handleChange}
            className="w-full p-4 bg-[#e0e5ec] rounded-xl shadow-[inset_10px_10px_20px_#a3b1c6,inset_-10px_-10px_20px_#ffffff] text-[#6d7582] placeholder-[#a3b1c6] focus:outline-none transition-all duration-300"
          />
          {errors.name && <p className="text-red-500 text-xs mt-1 ml-1">{errors.name}</p>}
        </div>
        
        {/* Email Input */}
        <div className="w-full mb-6">
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={formData.email}
            onChange={handleChange}
            className="w-full p-4 bg-[#e0e5ec] rounded-xl shadow-[inset_10px_10px_20px_#a3b1c6,inset_-10px_-10px_20px_#ffffff] text-[#6d7582] placeholder-[#a3b1c6] focus:outline-none transition-all duration-300"
          />
          {errors.email && <p className="text-red-500 text-xs mt-1 ml-1">{errors.email}</p>}
        </div>
        
        {/* Password Input */}
        <div className="w-full mb-6">
          <input
            type={showPassword ? "text" : "password"} // ðŸ‘ˆ toggle password visibility
            name="password"
            placeholder="Password"
            value={formData.password}
            onChange={handleChange}
            className="w-full p-4 bg-[#e0e5ec] rounded-xl shadow-[inset_10px_10px_20px_#a3b1c6,inset_-10px_-10px_20px_#ffffff] text-[#6d7582] placeholder-[#a3b1c6] focus:outline-none transition-all duration-300"
          />
          {}
          {errors.password && <p className="text-red-500 text-xs mt-1 ml-1">{errors.password}</p>}
        </div>
        
        {/* Phone Number Input */}
        <div className="w-full mb-8">
          <input
            type="text"
            name="phoneNumber"
            placeholder="Phone Number"
            value={formData.phoneNumber}
            onChange={handleChange}
            className="w-full p-4 bg-[#e0e5ec] rounded-xl shadow-[inset_10px_10px_20px_#a3b1c6,inset_-10px_-10px_20px_#ffffff] text-[#6d7582] placeholder-[#a3b1c6] focus:outline-none transition-all duration-300"
          />
          {errors.phoneNumber && <p className="text-red-500 text-xs mt-1 ml-1">{errors.phoneNumber}</p>}
        </div>
        
        {/* Submit Button */}
        <button
          type="submit"
          onClick={handleSubmit}
          className="px-8 py-4 bg-[#e0e5ec] rounded-xl shadow-[10px_10px_20px_#a3b1c6,-10px_-10px_20px_#ffffff] text-[#6d7582] hover:shadow-[0_0_10px_#4b658b,0_0_20px_#ad2424] hover:-translate-y-0.5 transition-all duration-300 active:shadow-[inset_5px_5px_10px_#a3b1c6,inset_-5px_-5px_10px_#ffffff]"
        >
          Sign Up
        </button>
        
        {/* Login Link */}
        <p className="text-[#6d7582] text-sm mt-6">
          Already have an account?{" "}
          <button 
            type="button" 
            onClick={navigateToLogin}
            className="text-[#6d7582] font-medium hover:underline focus:outline-none"
          >
            Login
          </button>
        </p>
      </div>
      
      {/* Background animation styles */}
      <style jsx global>{`
        body {
          margin: 0;
          height: 100vh;
          width: 100%;
          display: flex;
          justify-content: center;
          align-items: center;
          font-family: Arial, sans-serif;
          background: linear-gradient(-45deg, #3ba5b8, #b838a7, #7db345, #b65959);
          background-size: 400% 400%;
          animation: gradientBG 15s ease infinite;
        }
        @keyframes gradientBG {
          0% { background-position: 0% 50%; }
          50% { background-position: 100% 50%; }
          100% { background-position: 0% 50%; }
        }
      `}</style>
    </div>
  );
}

export default Signup;
