import React, { useState } from "react";
import axios from "axios";

function Signup({ navigateToLogin }) {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    phoneNumber: ""
  });

  const [errors, setErrors] = useState({
    name: "",
    email: "",
    password: "",
    phoneNumber: ""
  });

  // Validation functions
  const validateName = (name) => {
    if (!name) return "Name is required";
    if (name.length < 3) return "Name must be at least 3 characters";
    return "";
  };

  const validateEmail = (email) => {
    if (!email) return "Email is required";
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(email)) return "Enter a valid email address";
    return "";
  };

  const validatePassword = (password) => {
    if (!password) return "Password is required";
    if (password.length < 6) return "Password must be at least 6 characters";
    return "";
  };

  const validatePhoneNumber = (phone) => {
    if (!phone) return "Phone number is required";
    const phoneRegex = /^[0-9]{10}$/; // Exactly 10 digits
    if (!phoneRegex.test(phone)) return "Enter a valid 10-digit phone number";
    return "";
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });

    // Real-time validation
    if (name === "name") setErrors((prev) => ({ ...prev, name: validateName(value) }));
    if (name === "email") setErrors((prev) => ({ ...prev, email: validateEmail(value) }));
    if (name === "password") setErrors((prev) => ({ ...prev, password: validatePassword(value) }));
    if (name === "phoneNumber") setErrors((prev) => ({ ...prev, phoneNumber: validatePhoneNumber(value) }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Final validation before submission
    const nameError = validateName(formData.name);
    const emailError = validateEmail(formData.email);
    const passwordError = validatePassword(formData.password);
    const phoneError = validatePhoneNumber(formData.phoneNumber);

    if (nameError || emailError || passwordError || phoneError) {
      setErrors({
        name: nameError,
        email: emailError,
        password: passwordError,
        phoneNumber: phoneError
      });
      return;
    }

    try {
      await axios.post("http://localhost:5000/signup", formData);
      alert("Signup successful");
      navigateToLogin();
    } catch (error) {
      if (error.response?.status === 400) {
        if (window.confirm("User already exists. Do you want to login instead?")) {
          navigateToLogin();
        }
      }
    }
  };

  return (
    <div className="flex justify-center items-center h-screen bg-gray-100">
      <form
        onSubmit={handleSubmit}
        className="bg-white p-6 rounded shadow-md w-96"
      >
        <h2 className="text-2xl font-bold mb-4 text-center">Signup</h2>

        <input
          type="text"
          name="name"
          placeholder="Name"
          className={`w-full p-2 mb-1 border rounded ${errors.name ? "border-red-500" : ""}`}
          value={formData.name}
          onChange={handleChange}
        />
        {errors.name && <p className="text-red-500 text-sm mb-2">{errors.name}</p>}

        <input
          type="email"
          name="email"
          placeholder="Email"
          className={`w-full p-2 mb-1 border rounded ${errors.email ? "border-red-500" : ""}`}
          value={formData.email}
          onChange={handleChange}
        />
        {errors.email && <p className="text-red-500 text-sm mb-2">{errors.email}</p>}

        <input
          type="password"
          name="password"
          placeholder="Password"
          className={`w-full p-2 mb-1 border rounded ${errors.password ? "border-red-500" : ""}`}
          value={formData.password}
          onChange={handleChange}
        />
        {errors.password && <p className="text-red-500 text-sm mb-2">{errors.password}</p>}

        <input
          type="text"
          name="phoneNumber"
          placeholder="Phone Number"
          className={`w-full p-2 mb-1 border rounded ${errors.phoneNumber ? "border-red-500" : ""}`}
          value={formData.phoneNumber}
          onChange={handleChange}
        />
        {errors.phoneNumber && <p className="text-red-500 text-sm mb-2">{errors.phoneNumber}</p>}

        <button
          type="submit"
          className="w-full bg-blue-500 text-white p-2 rounded hover:bg-blue-600 mt-2"
        >
          Signup
        </button>
          <p className="text-center mt-4 text-sm">
          Already signup?Please Login{" "}
          <button
            type="button"
            onClick={navigateToLogin}
            className="text-blue-500 hover:red"
          >
            Login
          </button>
        </p>
      </form>
    </div>
  );
}

export default Signup;
