import { Eye, EyeOff } from "lucide-react"; // ðŸ‘ˆ optional icons library

function Login({ navigateToHome, navigateToSignup }) {
  const [formData, setFormData] = useState({
    email: "",
    password: ""
  });

  const [errors, setErrors] = useState({
    email: "",
    password: ""
  });

  const [showPassword, setShowPassword] = useState(false); // ðŸ‘ˆ state to toggle password visibility

  // ... your existing validation + functions

  return (
    <div className="min-h-screen w-full flex justify-center items-center font-sans">
      <div className="bg-[#e0e5ec] rounded-2xl p-10 flex flex-col items-center shadow-[10px_10px_20px_#a3b1c6,-10px_-10px_20px_#ffffff] hover:shadow-[12px_12px_24px_#a3b1c6,-12px_-12px_24px_#ffffff] transition-all duration-300 w-[450px]">
        <h1 className="text-[#6d7582] text-3xl font-semibold mb-8">Login</h1>
        
        {/* Email Input */}
        <div className="w-full mb-8">
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={formData.email}
            onChange={handleChange}
            className="w-full p-4 bg-[#e0e5ec] rounded-xl shadow-[inset_10px_10px_20px_#a3b1c6,inset_-10px_-10px_20px_#ffffff] text-[#6d7582] placeholder-[#a3b1c6] focus:outline-none transition-all duration-300 text-lg"
          />
          {errors.email && <p className="text-red-500 text-sm mt-2 ml-1">{errors.email}</p>}
        </div>
        
        {/* Password Input with Show/Hide */}
        <div className="w-full mb-10 relative">
          <input
            type={showPassword ? "text" : "password"} // ðŸ‘ˆ toggle type here
            name="password"
            placeholder="Password"
            value={formData.password}
            onChange={handleChange}
            className="w-full p-4 pr-12 bg-[#e0e5ec] rounded-xl shadow-[inset_10px_10px_20px_#a3b1c6,inset_-10px_-10px_20px_#ffffff] text-[#6d7582] placeholder-[#a3b1c6] focus:outline-none transition-all duration-300 text-lg"
          />
          {/* Toggle Button */}
          <button
            type="button"
            onClick={() => setShowPassword(!showPassword)}
            className="absolute right-4 top-1/2 -translate-y-1/2 text-[#6d7582] hover:text-[#4b5563] transition"
          >
            {showPassword ? <EyeOff size={20} /> : <Eye size={20} />}
          </button>

          {errors.password && <p className="text-red-500 text-sm mt-2 ml-1">{errors.password}</p>}
        </div>
        
        {/* Rest of your code (Login button + Signup link)... */}
      </div>
    </div>
  );
}
